import React from 'react';
import CustomerList from '../components/Customers/CustomerList';
import Request from '../helpers/request.js'

class CustomerContainer extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      data:[]
    };
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
  };

  componentDidMount(){
   let request = new Request()
   request.get('/api/customers').then((data) => {
     this.setState({data:data._embedded.customers})

   })
 }

 handleFormSubmit(name, phone, email){
 this.setState({name: name, phone: phone, email: email});

}

  render(){
    return(
      <>
      <CustomerForm onFormSubmit = {this.handleFormSubmit}/>
      <CustomerList data={this.state.data}/>
      </>
    );
  };
};

export default CustomerContainer;
