import React, {Component} from 'react';

class BookingForm extends Component{
  constructor(props){
    super(props);
    this.state={
      name:"",
      discount:"",
      time:"",
      table:"",
      partySize:""
    }

    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleDiscountChange = this.handleDiscountChange.bind(this);
    this.handleTimeChange = this.handleTimeChange.bind(this);
    this.handleTableChange = this.handleTableChange.bind(this);
    this.handlePartySizeChange = this.handlePartySizeChange.bind(this);
  }

  handleNameChange(event){
    this.setState({name:event.target.value});
  }
  handleDiscountChange(event){
    this.setState({discount:event.target.value});
  }
  handleTimeChange(event){
    this.setState({time:event.target.value});
  }
  handleTableChange(event){
    this.setState({table:event.target.value});
  }
  handlePartySizeChange(event){
    this.setState({partySize: event.target.value});
  };

  handleSubmit(event){
    event.preventDefault();
    const booking = {
      "date":this.state.date,
      "booth":this.state.table,
      "customer":this.state.customer,
      "PartySize":this.state.partySize,
      "timeSlot":this.state.time
    }




      this.props.onFormSubmit(booking);

      this.setState({name:"", discount:"", time:"", table:"", partySize:""});
  }



  render(){
    return(
      <>
        <h2>Create a booking</h2>
        <form
        id = "make-booking"
        onSubmit={this.handleSubmit}>

          <input
            className="make-booking"
            name="name"
            placeholder="Enter Name"
            type = "text"
            value = {this.state.name}
            onChange={(this.handleNameChange)}/>
          <select
            className="make-booking"
            placeholder="discount"
            name="discount">

            <option value="NIL">NIL</option>
            <option value="BRONZE">BRONZE</option>
            <option value="SILVER">SILVER</option>
            <option value="GOLD">GOLD</option>

            value = {this.state.discount}
            onChange={(this.handleDiscountChange)}
            </select>
          <input
            className="make-booking"
            placeholder="Enter Time"
            name="time"
            type = "text"
            value = {this.state.time}
            onChange={(this.handleTimeChange)}/>

            <input
              className="make-booking"
              placeholder="Enter Table"
              name="table"
              type = "table"
              value = {this.state.table}
              onChange={(this.handleTableChange)}/>

            <input
              className="make-booking"
              placeholder="Enter Party size"
              type = "number"
              name="partySize"
              value = {this.state.partySize}
              onChange={(this.handlePartySizeChange)}/>

            <input
              id="button"
              type="submit"
              value="Create Booking"/>

          </form>

      </>
    )
  }
}

export default BookingForm;
