import React, {Component} from 'react';
import ReceiptList from '../components/Receipts/ReceiptList';
import Request from '../helpers/request';

class ReceiptContainer extends Component {
  constructor(props){
    super(props);
    this.state = {
      data:[
        {0:{name:"Soup",quantity:0}},
        {1:{name:"PrawnCocktail",quantity:0}},
        {2:{name:"Haggis",quantity:0}},
        {3:{name:"Pizza",quantity:0}},
        {4:{name:"Chateaubriand",quantity:0}},
        {5:{name:"GoatsCheeseTart",quantity:0}},
        {6:{name:"MoltenLavaCake",quantity:0}},
        {7:{name:"BakedAlaska",quantity:0}},
        {8:{name:"CheeseAndBiscuis",quantity:0}},
        {9:{name:"SparklingWater",quantity:0}},
        {10:{name:"HouseRedWine",quantity:0}},
        {11:{name:"CokeOrPepsi",quantity:0}},
        {12:{name:"",quantity:0}}
      ]}
};

handleFormSubmit(quantity){
this.setState({quantity: quantity});
};

componentDidMount(){
   let request = new Request()
   request.get('/api/receipts').then((data) => {
     this.setState({data:data._embedded.receipts});
   })
 }

  render(){
    return(
      <>
      <ReceiptList data={this.state.data} onFormSubmit={this.handleFormSubmit}/>
      </>
    );
  };
};

export default ReceiptContainer;
