import React, {Component} from 'react';

class BookingForm extends Component{
  constructor(props){
    super(props);
    this.state={
      name:"",
      date:"",
      time:"",
      table:"",
      partySize:""
    }

    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleDateChange = this.handleDateChange.bind(this);
    this.handleTimeChange = this.handleTimeChange.bind(this);
    this.handleTableChange = this.handleTableChange.bind(this);
    this.handlePartySizeChange = this.handlePartySizeChange.bind(this);
  }



  handleSubmit(event){
    event.preventDefault();
    const booking = {
      "date":this.target.date.value,
      "booth":this.target.table.value,
      "customer":this.target.customer.value,
      "PartySize":this.target.partySize.value,
      "timeSlot":this.target.time.value
    }

    this.props.onFormSubmit(booking);

      this.setState({name:"", date:"", time:"", table:"", partySize:""});
  }



  render(){
    return(
      <>
        <h2>Create a booking</h2>
        <form
        id = "make-booking"
        onSubmit={this.handleSubmit}>

          <input
            className="make-booking"
            name="name"
            placeholder="Enter Name"
            type = "text"
            />

            <input
              className="make-booking"
              name="date"
              placeholder="Enter Date"
              type = "text"
              />

          <input
            className="make-booking"
            placeholder="Enter Time"
            name="time"
            type = "text"
            />

            <input
              className="make-booking"
              placeholder="Enter Table"
              name="table"
              type = "text"
              />

            <input
              className="make-booking"
              placeholder="Enter Party size"
              type = "number"
              name="partySize"
              />

            <input
              id="button"
              type="submit"
              value="Create Booking"/>

          </form>

      </>
    )
  }
}

export default BookingForm;
