import React, {Component} from 'react';
import TableList from '../components/Tables/TableList';
import TableForm from '../components/Tables/TableForm';
import Request from '../helpers/request';


class TableContainer extends Component {
  constructor(props){
    super(props);
    this.state = {
      date: "2018-12-11",
      time: 4,
      partySize: 8,
      data:[{
        id:1,
        restaurant_id:1,
        bookings:[],
        capacity:4,
        available:true
      },
      {
        id:2,
        restaurant_id:1,
        bookings:[],
        capacity:2,
        available:false
      }]
    };
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
  };

  handleFormSubmit(partySize, date, time){
  this.setState({partySize: partySize, date: date, time:time});

};

componentDidMount(){
   let request = new Request()
   request.get('/api/booths').then((data) => {
     this.setState({data: data._embedded.booths});
   })
 }

getAllBookings(){
  let allBookings = [];
  let requests = [];
  for(let i = 1; i < 7; i++){
    let request = new Request();
    requests.push(request.get(`/api/booths/${i}/bookings`))
  }
  Promise.all(requests).then(responses => {
    for (var table of responses){
      let tableMatch=[];
      for (var booking of table._embedded.bookings){
        if(booking.booth.capacity < this.state.partySize){
        tableMatch.push(booking.booth.id)
      }
      if((booking.date == this.state.date) & (booking.timeSlot == this.state.time)){
          tableMatch.push(booking.booth.id)
      }
    }
      if(tableMatch.length > 0){
        // http://localhost:8080/toggle_booth_availability/4
debugger;
        this.handleTableAvailabilityUpdate(table._embedded.bookings.booth.id, {available: "FALSE" })
      }
      this.handleTableAvailabilityUpdate(table._embedded.bookings.booth.id, { available: "TRUE"})
      allBookings=[];
      tableMatch=[];
    };
  });
};

  handleTableAvailabilityUpdate(booth, path){
   const url = '/api/booths/' + booth + '/available';
   let request = new Request();
   request.patch(url, path).then(data => {
     window.location = '/booths'
   })
 }

  render(){
    this.getAllBookings();
    return(
      <>
      <TableForm onFormSubmit = {this.handleFormSubmit}/>

      <TableList data={this.state.data}/>
      </>
    );
  };
};


export default TableContainer;
