import React, {Component} from 'react';
import ReceiptList from '../components/Receipts/ReceiptList';
import Request from '../helpers/request';

class ReceiptContainer extends Component {
  constructor(props){
    super(props);
    this.state = {
      data:[
        {Soup:{id:1,quantity:0}},
        {PrawnCocktail:{id:2,quantity:0}},
        {Haggis:{id:3,quantity:0}},
        {Pizza:{id:4,quantity:0}},
        {Chateaubriand:{id:5,quantity:0}},
        {GoatsCheeseTart:{id:6,quantity:0}},
        {MoltenLavaCake:{id:7,quantity:0}},
        {BakedAlaska:{id:8,quantity:0}},
        {CheeseAndBiscuis:{id:9,quantity:0}},
        {SparklingWater:{id:10,quantity:0}},
        {HouseRedWine:{id:11,quantity:0}},
        {CokeOrPepsi:{id:12,quantity:0}}
      ]}
};

handleFormSubmit(quantity){
this.setState({quantity: quantity});
};

componentDidMount(){
   let request = new Request()
   request.get('/api/receipts').then((data) => {
     this.setState({data:data._embedded.receipts});
   })
 }

  render(){
    return(
      <>
      <ReceiptList data={this.state.data} onFormSubmit={this.handleFormSubmit}/>
      </>
    );
  };
};

export default ReceiptContainer;
