import React, {Component}  from 'react';
import {Link} from 'react-router-dom';

class SingleBooking extends Component {
  constructor(props){
    super(props);
    this.state = {}
    }



onDelete(){
  this.props.handleDelete(this.props.booking.id)
  }

render(){

  if(!this.props.booking){
    return null;
  }




    return (
        <div className="component">
          <p>Name: {this.props.booking.customer.name}</p>
          <p>Time: {this.props.booking.timeSlotValue}</p>
          <p>Table: {this.props.booking.booth.id}</p>
          <p>Party size: {this.props.booking.partySize}</p>

          <button onClick={this.onDelete}>Delete Customer</button>

          <Link to={'/bookings/update/' + this.props.booking.id}>
          <button>Update Booking</button></Link>
        </div>
      )
    }


  }

  export default SingleBooking;
