import React, {Component} from 'react';

class BookingForm extends Component{
  constructor(props){
    super(props);
    const customerOptions = props.customerData.map((customer)=>{
    return <option key={customer.id} value={customer._links.self.href}>{customer.name}</option>
  })
    // this.state={
    //   name:"",
    //   date:"",
    //   time:"",
    //   table:"",
    //   partySize:""
    // }
this.handleSubmit = this.handleSubmit.bind(this);

  }



  handleSubmit(event){
    event.preventDefault();
    const booking = {
      "date":event.target.date.value,
      "booth":event.target.table.value,
      "name":event.target.name.value,
      "PartySize":event.target.partySize.value,
      "timeSlot":event.target.time.value
    }
debugger;
    this.props.onFormSubmit(booking);

      // this.setState({name:"", date:"", time:"", table:"", partySize:""});
  }



  render(){
    return(
      <>
        <h2>Create a booking</h2>
        <form
        id = "make-booking"
        onSubmit={this.handleSubmit}>

          <select
            className="make-booking"
            name="name"
            placeholder="Choose Customer Name"
            type = "customer">
            {customerOptions}
            </select>

            <input
              className="make-booking"
              name="date"
              placeholder="Enter Date"
              type = "text"
              />

          <input
            className="make-booking"
            placeholder="Enter Time"
            name="time"
            type = "text"
            />

            <input
              className="make-booking"
              placeholder="Enter Table"
              name="table"
              type = "text"
              />

            <input
              className="make-booking"
              placeholder="Enter Party size"
              type = "number"
              name="partySize"
              />

            <input
              id="button"
              type="submit"
              value="Create Booking"/>

          </form>

      </>
    )
  }
}

export default BookingForm;
