render(){
  this.getAllBookings()
    if(this.state.available===true){

    return(
      <button
        id = "table-button-green"
        className = "table-button"
        type = "submit">
        TABLE {this.props.id} (Seats {this.props.capacity})
      </button>
    )}else{;

    return(
      <button
        id = "table-button-red"
        className = "table-button"
        type = "button" disabled>
        TABLE {this.props.id} (Seats {this.props.capacity})
      </button>
    );
    };
  };
};

export default Table;
